import {withRouter} from 'next/router'
import {Row,Col,List} from 'antd'  //栅格
import Link from 'next/link'
import Router from 'next/router'

import api from '../lib/api'

//搜索条件
const LANGUAGE=['JavaScript','HTML','CSS','TypeScript','Java','Rust']
const SORT_TYPES=[
  {
    name:'Best Match'
  },
  {
    name:'Most Stars',
    value:'stars',
    order:'desc'
  },
  {
    name:'Fewest Stars',
    value:'stars',
    order:'asc'
  },
  {
    name:'Most Forks',
    value:'forks',
    order:'desc'
  },
  {
    name:'Fewest Forks',
    value:'forks',
    order:'asc'
  },
]

/**
 * sort：排序方式
 * order：排序顺序（升/降序）
 * lang：仓库的项目开发主语言
 * page：分页页面
 */

 const selectedItemStyle={
   borderLeft:'2px solid #e36209',
   fontWeight:100
 }

function Search({router,repos}){
  console.log(repos)
  
  const {sort,order,lang,query}=router.query
  const handleLanguageChange=(language)=>{
    Router.push({
      pathname:'/search',
      query:{
        query,
        lang:language,
        sort,
        order,
      }
    })
  }
  const handleSortChange=(sort)=>{
    Router.push({
      pathname:'/search',
      query:{
        query,
        lang,
        sort:sort.value,
        order:sort.order,
      }
    })
  }
  return (
    <div className="root">
      <Row gutter={20}>
        <Col span={6}>
           <List 
             bordered
             header={<span className="list-header">语言</span>}
             style={{marginBottom:20}}
             dataSource={LANGUAGE}
             renderItem={item=>{
               const selected=lang===item
               return (
                 <List.Item style={selected?selectedItemStyle:null}>
                    <a onClick={()=>handleLanguageChange(item)}>{item}</a>
                 </List.Item>
               )
             }}
           />
            <List 
             bordered
             header={<span className="list-header">排序</span>}
             style={{marginBottom:20}}
             dataSource={SORT_TYPES}
             renderItem={item=>{
               return (
                 <List.Item>
                    <a onClick={()=>handleSortChange(item)}>{item.name}</a>
                 </List.Item>
               )
             }}
           />
        </Col>
      </Row>
    </div>
  )
  
}

Search.getInitialProps=async ({ctx})=>{
  const {query,sort,order,lang,page}=ctx.query;
  if(!query){
    return {
      repos:{
        total_count:0
      }
    }
  }
  //?q=react+language:javascript&sort=stars&order=desc&page=2
  let queryString=`?q=${query}`
  if(lang) queryString+=`+language:${lang}`
  if(sort) queryString+=`&sort=${sort}&order=${order||'desc'}`
  if(page) queryString+=`&page=${page}`
  //调用搜索列表接口
  const result=await api.request({
    url:`/search/repositories${queryString}`
  },ctx.req,ctx.res)

  return {
    repos:result.data
  }
}

export default withRouter(Search)
