import { useState, useCallback } from 'react'
import Link from 'next/link'
import { Button, Layout, Icon, Input, Avatar } from 'antd'

const { Header, Content, Footer } = Layout

import Container from './Container'

//style写在外边原因：githubIconStyle 保证对象为同一个 避免重新渲染
const githubIconStyle = {
  color: 'white',
  fontSize: 40,
  display: 'block',
  paddingTop: 10,
  marginRight: 20,
}
const footerStyle = {
  textAlign: 'center'
}

// const Comp = ({ color, children, style }) => <div style={{ color, ...style }}>{children}</div>

export default ({ children }) => {
  const [search, setSearch] = useState('');
  const handleSearchChange = useCallback((event) => {
    setSearch(event.target.value)
  }, [setSearch])
  const handleOnSearch = useCallback(() => { }, [])
  return (
    <Layout>
      <Header>
        <Container renderer={<div className="header-inner" />}>
          <div className="header-left">
            <div className="logo">
              <Icon type="github" style={githubIconStyle} />
            </div>
            <div>
              <Input.Search placeholder="搜索仓库" value={search} onChange={handleSearchChange} onSearch={handleOnSearch} />
            </div>
          </div>
          <div className="header-right">
            <div className="user">
              <Avatar size={40} icon="user" />
            </div>
          </div>
        </Container>
      </Header>
      <Content>
        <Container>{children}</Container>
      </Content>
      <Footer style={footerStyle}>
        develop by JinYoMo@<a href="https://github.com/JinYoMo/coco-github">coco-github</a>
      </Footer>
      <style jsx>{`
        .content{
          color:red;
        }
         .header-inner{
           display:flex;
           justify-content:space-between; 
         }
         .header-left{
           display:flex;
           justify-content:flex-start;
         }
      `}</style>
      <style jsx global>{`
        #__next{
          height:100%;
        }
        .ant-layout{
          height:100%;
        }
        .ant-layout-header{
          paddingLeft:0;
          paddingRight:0;
        }
      `}
      </style>
    </Layout>
  )
}
