import { cloneElement } from 'react'

const style = {
  width: '100%',
  maxWidth: 1200,
  marginLeft: 'auto',
  marginRight: 'auto'
}

//cloneElement将renderer进行拷贝，然后增加了一些props，以此来扩展想要渲染的renderer节点
export default ({ children, renderer }) => {
  const newElement = cloneElement(renderer, {
    style, children
  })
  return newElement
}
