import {memo} from 'react'
import {withRouter} from 'next/router'
import {Row,Col,List} from 'antd'  //栅格
import Link from 'next/link'
import Router from 'next/router'

import Repo from '../components/Repo'

import api from '../lib/api'

//搜索条件
const LANGUAGE=['JavaScript','HTML','CSS','TypeScript','Java','Rust']
const SORT_TYPES=[
  {
    name:'Best Match'
  },
  {
    name:'Most Stars',
    value:'stars',
    order:'desc'
  },
  {
    name:'Fewest Stars',
    value:'stars',
    order:'asc'
  },
  {
    name:'Most Forks',
    value:'forks',
    order:'desc'
  },
  {
    name:'Fewest Forks',
    value:'forks',
    order:'asc'
  },
]

/**
 * sort：排序方式
 * order：排序顺序（升/降序）
 * lang：仓库的项目开发主语言
 * page：分页页面
 */

 const selectedItemStyle={
   borderLeft:'2px solid #e36209',
   fontWeight:100
 }
//memo方法作用：name,query,lang,sort,order 这些参数无变化，则不会被重新渲染
 const FilterLink=memo(({name,query,lang,sort,order})=>{
    let queryString=`?query=${query}`
    if(lang) queryString+=`&lang=${lang}`
    if(sort) queryString+=`&sort=${sort}&order=${order||'desc'}`

    return <Link href={`/search${queryString}`}><a>{name}</a></Link>
 })
function Search({router,repos}){
  console.log(repos)
  
  // const {sort,order,lang,query}=router.query
  const {...querys}=router.query
  const {sort,order,lang}=router.query
  return (
    <div className="root">
      <Row gutter={20}>
        <Col span={6}>
           <List 
             bordered
             header={<span className="list-header">语言</span>}
             style={{marginBottom:20}}
             dataSource={LANGUAGE}
             renderItem={item=>{
               const selected=lang===item
               return (
                 <List.Item style={selected?selectedItemStyle:null}>
                   {selected?<span>{item}</span>:<FilterLink {...querys} lang={item} name={item}/>}
                 </List.Item>
               )
             }}
           />
            <List 
             bordered
             header={<span className="list-header">排序</span>}
             dataSource={SORT_TYPES}
             renderItem={item=>{
               let selected=false
               if(item.name==='Best Match'&&!sort){
                 selected=true
               }else if(item.value===sort&&item.order===order){
                 selected=true
               }
               return (
                <List.Item style={selected?selectedItemStyle:null}>
                  {selected?<span>{item.name}</span>:<FilterLink {...querys} sort={item.value} order={item.order} name={item.name}/>}
                 </List.Item>
               )
             }}
           />
        </Col>
        <Col span={18}>
            <h3 className="repos-title">{repos.total_count} 个仓库</h3>
            {
              // repos.items.map(repo => <Repo repo={repo} key={repo.id} />)
            }
        </Col>
      </Row>
      <style jsx>{`
        .root{
          padding:20px 0;
        }
        .list-header{
          font-weight:800;
          font-size:16px;
        }
        `}</style>
    </div>
  )
  
}

Search.getInitialProps=async ({ctx})=>{
  const {query,sort,order,lang,page}=ctx.query;
  if(!query){
    return {
      repos:{
        total_count:0
      }
    }
  }
  //?q=react+language:javascript&sort=stars&order=desc&page=2
  let queryString=`?q=${query}`
  if(lang) queryString+=`+language:${lang}`
  if(sort) queryString+=`&sort=${sort}&order=${order||'desc'}`
  if(page) queryString+=`&page=${page}`
  //调用搜索列表接口
  const result=await api.request({
    url:`/search/repositories${queryString}`
  },ctx.req,ctx.res)

  return {
    repos:result.data
  }
}

export default withRouter(Search)
