import dynamid from 'next/dynamic'
import {useState,useCallback} from 'react'
import {Avatar,Button,Select} from 'antd'
import withRepoBasic from '../../components/with-repo-basic'
import SearchUser from '../../components/SearchUser'

import api from '../../lib/api'

import { getLastUpdated } from '../../lib/utils'

const MDRenderer = dynamid(()=>import('../../components/MarkdownRenderer'),{
  loading:()=><p>Loading</p>
})

function IssueDetail({issue}){
  return (
    <div className="root">
      <MDRenderer content={issue.body} />
      <div className="actions">
        <Button href={issue.html_url} target="_blank">打开Issue讨论页面</Button>
      </div>
      <style jsx>{`
        .root {
          background: #fefefe;
          padding: 20px;
        }
        .actions {
          text-align: right;
        }
      `}</style>
    </div>
  )
}

function IssueItem({issue}){
  const [showDetail,setShowDetail]=useState(false)

  const toggleShowDetail=useCallback(()=>{
    setShowDetail(detail=>!detail)
  },[])

   return (
     <div>
        <div className="issue">
          <Button 
              type="primary" 
              size="small" 
              style={{position:'absolute',right:10,top:10}}
              onClick={toggleShowDetail}
              >{showDetail?'隐藏':'查看'}</Button>
          <div className="avatar">
            <Avatar src={issue.user.avatar_url} shape="square" size={50} />
          </div>
          <div className="main-info">
            <h6>
              <span>{issue.title}</span>
            </h6>
            <p className="sub-info">
              <span>Updated at {getLastUpdated(issue.updated_at)}</span>
            </p>
          </div>
      <style jsx>{`
          .issue {
            display: flex;
            position: relative;
            padding: 10px;
          }
          .issue:hover {
            background: #fafafa;
          }
          .issue + .issue {
            border-top: 1px solid #eee;
          }
          .main-info > h6 {
            max-width: 600px;
            font-size: 16px;
            padding-right: 40px;
          }
          .avatar {
            margin-right: 20px;
          }
          .sub-info {
            margin-bottom: 0;
          }
          .sub-info > span + span {
            display: inline-block;
            margin-left: 20px;
            font-size: 12px;
          }
      `}</style>
      </div>
      {showDetail?<IssueDetail issue={issue} />:null}
    </div>
   )
}

function makeQuery(creator,state,labels){
   let creatorStr=creator?`creator=${creator}`:''
   let stateStr=state?`state=${state}`:''
   let labelStr=''
   if(labels&&labels.length>0){
     labelStr=`labels=${labels.join(',')}`
   }
   const arr=[];
   if(creatorStr) arr.push(creatorStr)
   if(stateStr) arr.push(stateStr)
   if(labelStr) arr.push(labelStr)
    
   return `?${arr.join('&')}`
}

const Option=Select.Option
function Issues ({initialIssues,labels}){
  console.log(initialIssues,labels)

  const [creator,setCreator]=useState()
  const [state,setState]=useState()
  const [label,setLabel]=useState([])
  const [issues,setIssues]=useState(initialIssues)

  const handleCreatorChange=useCallback((value)=>{
    setCreator(value)
  },[])

  const handleStateChange=useCallback((value)=>{
    setState(value)
  },[])

  const handleLabelChange=useCallback((value)=>{
    setLabel(value)
  },[])

  const handleSearch=()=>{
    //search
    api.request({
      url:`/repos/${owner}/${name}/issues${makeQuery(creator,state,labels)}`
    },ctx.req,ctx.res)
    .then(resp=>setIssues(resp.data))
  }

  return (
     <div className="root">
       <div className="search">
          <SearchUser onChange={handleCreatorChange} value={creator} />
          <Select placeholder="状态" onChange={handleStateChange} style={{width:200,marginLeft:20}} value={state}>
              <Option value="all">all</Option>
              <Option value="open">open</Option>
              <Option value="closed">closed</Option>
          </Select>
          <Select mode="multiple" placeholder="Label" onChange={handleLabelChange} style={{flexGrow:1,marginLeft:20,marginRight:20}} value={label}>
              {
                labels.map(la=><Option value={la.name} key={la.id}>{la.name}</Option>)
              }
          </Select>
          <Button type="primary" onClick={handleSearch}>搜索</Button>
       </div>
       <div className="issues">
          {issues.map(issue=><IssueItem issue={issue} key={issue.id} />)}
       </div>
       <style jsx>{`
        .issues {
          border: 1px solid #eee;
          border-radius: 5px;
          margin-bottom: 20px;
          margin-top: 20px;
        }
        .search {
          display: flex;
        }
        .loading {
          height: 400px;
          display: flex;
          align-items: center;
          justify-content: center;
        }
      `}</style>
     </div>
  )
}

Issues.getInitialProps=async({ctx})=>{
  const {owner,name}=ctx.query
  
  //Promise.all可以让两个异步执行的操作并行执行 互不干扰
  const fetchs=await Promise.all([
    await api.request({
      url:`/repos/${owner}/${name}/issues`
    },ctx.req,ctx.res),
    await api.request({
      url:`/repos/${owner}/${name}/labels`
    },ctx.req,ctx.res)
  ])


  return {
    owner,
    name,
    initialIssues:fetchs[0].data,
    labels:fetchs[1].data
  }
}

export default withRepoBasic(Issues,'issues')
